                if (y >= 2 * SCALE) {
                    // Add the 2^(-m) factor to the logarithm.
                    result += delta;

                    // Corresponds to z/2 on Wikipedia.
                    y >>= 1;
                }
            }
        }
    }

    /// @notice Multiplies two unsigned 60.18-decimal fixed-point numbers together, returning a new unsigned 60.18-decimal
    /// fixed-point number.
    /// @dev See the documentation for the "PRBMath.mulDivFixedPoint" function.
    /// @param x The multiplicand as an unsigned 60.18-decimal fixed-point number.
    /// @param y The multiplier as an unsigned 60.18-decimal fixed-point number.
    /// @return result The product as an unsigned 60.18-decimal fixed-point number.
    function mul(uint256 x, uint256 y) internal pure returns (uint256 result) {
        result = PRBMath.mulDivFixedPoint(x, y);
    }

    /// @notice Returns PI as an unsigned 60.18-decimal fixed-point number.
    function pi() internal pure returns (uint256 result) {
        result = 3_141592653589793238;
    }

    /// @notice Raises x to the power of y.
    ///
    /// @dev Based on the insight that x^y = 2^(log2(x) * y).
    ///
    /// Requirements:
    /// - All from "exp2", "log2" and "mul".
    ///
    /// Caveats:
    /// - All from "exp2", "log2" and "mul".
    /// - Assumes 0^0 is 1.
    ///
    /// @param x Number to raise to given power y, as an unsigned 60.18-decimal fixed-point number.
    /// @param y Exponent to raise x to, as an unsigned 60.18-decimal fixed-point number.
    /// @return result x raised to power y, as an unsigned 60.18-decimal fixed-point number.
    function pow(uint256 x, uint256 y) internal pure returns (uint256 result) {
        if (x == 0) {
            result = y == 0 ? SCALE : uint256(0);
        } else {
            result = exp2(mul(log2(x), y));
        }
    }

    /// @notice Raises x (unsigned 60.18-decimal fixed-point number) to the power of y (basic unsigned integer) using the
    /// famous algorithm "exponentiation by squaring".
    ///
    /// @dev See https://en.wikipedia.org/wiki/Exponentiation_by_squaring
    ///
    /// Requirements:
    /// - The result must fit within MAX_UD60x18.
    ///
    /// Caveats:
    /// - All from "mul".
    /// - Assumes 0^0 is 1.
    ///
    /// @param x The base as an unsigned 60.18-decimal fixed-point number.
    /// @param y The exponent as an uint256.
    /// @return result The result as an unsigned 60.18-decimal fixed-point number.
    function powu(uint256 x, uint256 y) internal pure returns (uint256 result) {
        // Calculate the first iteration of the loop in advance.
        result = y & 1 > 0 ? x : SCALE;

        // Equivalent to "for(y /= 2; y > 0; y /= 2)" but faster.
        for (y >>= 1; y > 0; y >>= 1) {
            x = PRBMath.mulDivFixedPoint(x, x);

            // Equivalent to "y % 2 == 1" but faster.
            if (y & 1 > 0) {
                result = PRBMath.mulDivFixedPoint(result, x);
            }
        }
    }

    /// @notice Returns 1 as an unsigned 60.18-decimal fixed-point number.
    function scale() internal pure returns (uint256 result) {
        result = SCALE;
    }

    /// @notice Calculates the square root of x, rounding down.
    /// @dev Uses the Babylonian method https://en.wikipedia.org/wiki/Methods_of_computing_square_roots#Babylonian_method.
    ///
    /// Requirements:
    /// - x must be less than MAX_UD60x18 / SCALE.
    ///
    /// @param x The unsigned 60.18-decimal fixed-point number for which to calculate the square root.
    /// @return result The result as an unsigned 60.18-decimal fixed-point .
    function sqrt(uint256 x) internal pure returns (uint256 result) {
        unchecked {
            if (x > MAX_UD60x18 / SCALE) {
                revert PRBMathUD60x18__SqrtOverflow(x);
            }
            // Multiply x by the SCALE to account for the factor of SCALE that is picked up when multiplying two unsigned
            // 60.18-decimal fixed-point numbers together (in this case, those two numbers are both the square root).
            result = PRBMath.sqrt(x * SCALE);
        }
    }

    /// @notice Converts a unsigned 60.18-decimal fixed-point number to basic integer form, rounding down in the process.
    /// @param x The unsigned 60.18-decimal fixed-point number to convert.
    /// @return result The same number in basic integer form.
    function toUint(uint256 x) internal pure returns (uint256 result) {
        unchecked {
            result = x / SCALE;
        }
    }
}
","keccak256":"0x2180489de7680a79844dd5ef145f800e4f68e515e0e89409242f0187dbbd657b","license":"Unlicense"},"contracts/implementations/DonationsRouter.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.13;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/proxy/Clones.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@prb/math/contracts/PRBMathUD60x18.sol";
import "../interfaces/IDonationsRouter.sol";
import "../interfaces/IStakingRewards.sol";
import "../interfaces/IThinWallet.sol";
import "./Queue.sol";

contract DonationsRouter is IDonationsRouter, Ownable, Queue {
    using PRBMathUD60x18 for uint256;

    ERC20 public immutable override baseToken;
    IStakingRewards public immutable override stakingContract;

    address public immutable override walletImplementation;

    uint256 public override causeId;
    uint256 public override platformFee;

    /// Cause ID => Cause record
    mapping(uint256 => CauseRecord) public override causeRecords;

    /// Thin wallet salt => thin wallet address
    mapping(bytes32 => address) public override deployedWallets;

    constructor(
        address _baseToken,
        address _stakingContract,
        address _owner,
        address _walletImplementation
    ) {
        require(_baseToken != address(0), "invalid base token");
        require(_stakingContract != address(0), "invalid staking contract");
        require(_owner != address(0), "invalid owner");
        require(_walletImplementation != address(0), "invalid implementation");

        baseToken = ERC20(_baseToken);
        stakingContract = IStakingRewards(_stakingContract);
        walletImplementation = _walletImplementation;

        _transferOwnership(_owner);
    }

    function setPlatformFee(uint256 _fee) external override onlyOwner {
        emit UpdateFee(platformFee, _fee);
        platformFee = _fee;
    }

    function registerCause(CauseRegistrationRequest calldata _cause)
        external
        override
    {
        require(_cause.owner != address(0), "invalid owner");
        require(_cause.daoToken != address(0), "invalid token");
        uint256 id = ++causeId; // Increments then returns, thus causeId starts at 1

        CauseRecord memory cause = CauseRecord({
            owner: _cause.owner,
            rewardPercentage: _cause.rewardPercentage,
            daoToken: _cause.daoToken,
            defaultWallet: calculateThinWallet(
                ThinWalletID({causeId: id, thinWalletId: abi.encode(id)})
            )
        });

        causeRecords[id] = cause;

        emit RegisterCause(_cause.owner, _cause.daoToken, id);

        address[] memory owners = new address[](1);
        owners[0] = _cause.owner;
        _deployWallet(
            _getSalt(ThinWalletID({causeId: id, thinWalletId: abi.encode(id)})),
            owners
        );

        ERC20(_cause.daoToken).approve(
            address(stakingContract),
            type(uint256).max
        );
    }

    function updateCause(uint256 _causeId, CauseUpdateRequest calldata _cause)
        external
        override
    {
        require(_causeId <= causeId, "invalid cause");
        CauseRecord memory cause = causeRecords[_causeId];
        require(msg.sender == cause.owner, "not authorized");
        require(_cause.owner != address(0), "invalid owner");

        cause.owner = _cause.owner;
        cause.rewardPercentage = _cause.rewardPercentage;

        causeRecords[_causeId] = cause;

        emit UpdateCause(cause);
    }

    function calculateThinWallet(ThinWalletID memory _walletId)
        public
        view
        override
        returns (address wallet)
    {
        wallet = Clones.predictDeterministicAddress(
            walletImplementation,
            _getSalt(_walletId)
        );
    }

    function registerThinWallet(
        ThinWalletID calldata _walletId,
        address[] calldata _owners
    ) external override {
        bytes32 salt = _getSalt(_walletId);
        require(deployedWallets[salt] == address(0), "already deployed");
        require(_owners.length >= 1, "invalid owners");
        CauseRecord memory cause = causeRecords[_walletId.causeId];

        require(_walletId.causeId <= causeId, "invalid cause");
        require(msg.sender == cause.owner, "unauthorized");

        address wallet = calculateThinWallet(_walletId);

        emit RegisterWallet(wallet, _walletId);

        _deployWallet(salt, _owners);
    }

    function withdrawFromThinWallet(
        ThinWalletID calldata _walletId,
        WithdrawalRequest calldata _withdrawal,
        bytes32 _proposalId
    ) external override {
        require(_walletId.causeId <= causeId, "invalid cause");
        CauseRecord memory cause = causeRecords[_walletId.causeId];

        require(msg.sender == cause.owner, "unauthorized");

        require(_proposalId != '', "invalid proposal id");
        uint128 queueToWithdraw = getFront(causeId);
        QueueItem memory item = getQueueItem(causeId, queueToWithdraw);
        if (item.isUnclaimed 
            && item.id == keccak256(abi.encode(causeId, _proposalId)
        )) {
            dequeue(causeId);
        }else{
            revert("not head of queue");
        }

        bytes32 salt = _getSalt(_walletId);
        IThinWallet wallet = IThinWallet(deployedWallets[salt]);

        if (address(wallet) == address(0)) {
            address[] memory owners = new address[](1);
            owners[0] = cause.owner;
            wallet = IThinWallet(_deployWallet(salt, owners));

            emit RegisterWallet(address(wallet), _walletId);
        }

        emit WithdrawFromWallet(_walletId, _withdrawal);

        address rewardToken = address(baseToken);

        if (_withdrawal.token == rewardToken) {
            uint256 rewardAmount = _withdrawal.amount.mul(
                cause.rewardPercentage
            );
            uint256 feeAmount = _withdrawal.amount.mul(platformFee);

            IThinWallet.TokenMovement[]
                memory transfers = new IThinWallet.TokenMovement[](3);
            transfers[0] = IThinWallet.TokenMovement({
                token: rewardToken,
                recipient: owner(),
                amount: feeAmount
            });
            transfers[1] = IThinWallet.TokenMovement({
                token: rewardToken,
                recipient: _withdrawal.recipient,
                amount: _withdrawal.amount - (feeAmount + rewardAmount)
            });
            transfers[2] = IThinWallet.TokenMovement({
                token: rewardToken,
                recipient: address(this),
                amount: rewardAmount
            });

            wallet.transferERC20(transfers);

            (stakingContract.rewardToken()).increaseAllowance(
                address(stakingContract),
                rewardAmount
            );
            stakingContract.distributeRewards(cause.daoToken, rewardAmount);
        } else {
            IThinWallet.TokenMovement[]
                memory transfers = new IThinWallet.TokenMovement[](1);
            transfers[0] = IThinWallet.TokenMovement({
                token: rewardToken,
                recipient: _withdrawal.recipient,
                amount: _withdrawal.amount
            });
            wallet.transferERC20(transfers);
        }
    }

    function addToQueue(uint256 _causeId, bytes32 _proposalId) external {
        require(_proposalId != bytes32(0), "invalid proposal id");
        CauseRecord memory cause = causeRecords[_causeId];
        require(msg.sender == cause.owner, "unauthorized");
        bytes32 queueId = keccak256(abi.encode(_causeId, _proposalId));
        enqueue(causeId, queueId);
    }

    function removeFromQueue(uint256 _causeId, bytes32 _proposalId, uint128 _index) external {
        CauseRecord memory cause = causeRecords[_causeId];
        require(msg.sender == cause.owner, "unauthorized");

        bytes32 queueId = keccak256(abi.encode(_causeId, _proposalId));
        QueueItem memory item = getQueueItem(causeId, _index);
        require(item.id == queueId, "id does not match index item");

        dequeue(_causeId, _index);
    }

    function getQueueAtIndex(uint256 _causeId, uint128 _index) 
        external 
        view  
        returns (QueuedItem memory item)
    {
        QueueItem memory retrievedItem = getQueueItem(_causeId, _index);
        item = QueuedItem({
            next: retrievedItem.next,
            previous: retrievedItem.previous,
            id : retrievedItem.id,
            isUnclaimed : retrievedItem.isUnclaimed
        });
    }

    function getFirstInQueue(uint256 _causeId) 
        external 
        view 
        returns (uint128 queueFront)
    {
        queueFront = getFront(_causeId);
    }

    function getLastInQueue(uint256 _causeId) 
        external
        view 
        returns (uint128 queueBack)
    {
        queueBack = getBack(_causeId);
    }

    /// ### Internal functions
    function _getSalt(ThinWalletID memory _walletId)
        internal
        pure
        returns (bytes32 salt)
    {
        salt = keccak256(abi.encode(_walletId));
    }

    function _deployWallet(bytes32 salt, address[] memory owners)
        internal
        returns (address wallet)
    {
        wallet = Clones.cloneDeterministic(walletImplementation, salt);

        deployedWallets[salt] = wallet;

        IThinWallet(wallet).initialize(address(this), owners);
    }
}
","keccak256":"0x2d56d978570cef70c8f844ea2787b5487039e2ada76f9e696f0975156ef024a5","license":"MIT"},"contracts/implementations/Queue.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.13;

abstract contract Queue {
    struct QueueManager {
        uint128 front;
        uint128 back;
    }
    struct QueueItem {
        uint128 next; // The next item to be claimed in the queue. If this is the last item (at back of queue), next should be 0
        uint128 previous; // The previous item in the queue. If this is the first item (at the front of the queue), previous should be 0
        bytes32 id; // An unique identifier for the queue item. This will link the add to queue and withdrawal functions.
        bool isUnclaimed; // Set to true when enqueuing. We can delete the struct when dequeueing, saving some gas.
    }

    // Cause ID => queue manager
    mapping(uint256 => QueueManager) private queueManagers;

    // Queue is operating in reverse and simulates an array with a mapping for gas reasons. It's an implementation of a doubly linked list.
    // [(front) 1][2][(back) 3] (add item) => [(front) 1][2][3][(back) 4]
    // [(front) 1][2][(back) 3] (remove item) => [(front) 2][(back) 3]
    // keccak(causeID, index) => queue item
    mapping(bytes32 => QueueItem) private queueItems;

    /// @notice Adds a new item to the cause queue
    /// @dev _id should not be reused
    /// @param _causeId  The cause to queue an item for
    /// @param _id  The unique ID to use when linking enqueue and withdrawal transactions
    function enqueue(uint256 _causeId, bytes32 _id) internal {
        uint128 currentHead = getFront(_causeId);
        uint128 currentTail = getBack(_causeId);
        uint128 newTail = currentTail + 1;

        if (currentHead == 0){
            currentHead = newTail;
        }

        queueItems[keccak256(abi.encode(_causeId, newTail))] = QueueItem({
            next: 0,
            previous: currentTail,
            id: _id,
            isUnclaimed: true
        });

        if (currentTail != 0) {
            QueueItem memory item = getQueueItem(_causeId, currentTail);
            uint128 prev = item.previous;
            queueItems[keccak256(abi.encode(_causeId, currentTail))] = QueueItem({
                next: newTail,
                previous: prev,
                id: _id,
                isUnclaimed: true
            });
        }

        queueManagers[_causeId] = QueueManager({
            front: currentHead,
            back: newTail
        });
    }

    /// @notice Removes an item from the queue
    /// @param _causeId  the cause to dequeue an item for
    function dequeue(uint256 _causeId) internal {
        _removeFromQueue(_causeId, getFront(_causeId));
    }

    /// @notice Removes an arbitrarily placed item from the queue
    /// @param _causeId  the cause to dequeue an item for
    /// @param _index  The index of the item to dequeue. This should be the queue front, unless it's being used to remove an arbitrarily located item from the queue (ie from the middle)
    function dequeue(uint256 _causeId, uint128 _index) internal {
        _removeFromQueue(_causeId, _index);
    }

    /// @notice Removes an item from the cause queue
    /// @param _causeId  the cause to dequeue an item for
    /// @param _index  The index of the item to dequeue. This should be the queue front, unless it's being used to remove an arbitrarily located item from the queue (ie from the middle)
    function _removeFromQueue(uint256 _causeId, uint128 _index) private {
        uint128 currentHead = getFront(_causeId);
        uint128 currentTail = getBack(_causeId);

        bytes32 id = keccak256(abi.encode(_causeId, _index));

        QueueItem memory item = getQueueItem(_causeId, _index);
        uint128 next = item.next;
        uint128 previous = item.previous;

        QueueManager storage manager = queueManagers[_causeId];
        uint128 newPrevious = next; uint128 newNext = previous;
        if (_index == currentHead){
            newPrevious = 0;
            manager.front = next;
        }
        if(_index == currentTail){
            newNext = 0;
            manager.back = previous;
        }

        queueItems[keccak256(abi.encode(_causeId, next))].previous = newPrevious;
        queueItems[keccak256(abi.encode(_causeId, previous))].next = newNext;

        delete queueItems[id];
    }

    /// @notice Returns the current ID at the front of the queue
    function getFront(uint256 _causeId)
        internal
        view
        returns (uint128 queueFront)
    {
        QueueManager memory manager = queueManagers[_causeId];
        queueFront = manager.front;
    }

    /// @notice Returns the current ID at the back of the queue
    function getBack(uint256 _causeId)
        internal
        view
        returns (uint128 queueBack)
    {
        QueueManager memory manager = queueManagers[_causeId];
        queueBack = manager.back;
    }

    /// @notice Returns the queue item at a given index
    function getQueueItem(uint256 _causeId, uint128 _index)
        internal
        view
        returns (QueueItem memory item)
    {
        item = queueItems[keccak256(abi.encode(_causeId, _index))];
    }
}","keccak256":"0x760b3fb0661e3e3968f6993e3f2bdcdfc0e4381df56ec0a73bf8fdfdddec114f","license":"MIT"},"contracts/interfaces/IDonationsRouter.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.13;
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

import "../interfaces/IStakingRewards.sol";

interface IDonationsRouter {
    /// ### Structs
    struct CauseRegistrationRequest {
        address owner;
        uint256 rewardPercentage;
        address daoToken;
    }
    struct CauseUpdateRequest {
        address owner;
        uint256 rewardPercentage;
    }

    struct CauseRecord {
        address owner;
        address defaultWallet; /// Default wallet is calculated with cause Id and thin wallet id being equal to each other
        address daoToken;
        uint256 rewardPercentage; /// A PRBMath 60.18 fixed point number. 1e16 == 1% and 1e18 == 100%
    }

    struct WithdrawalRequest {
        address token;
        address recipient;
        uint256 amount;
    }

    struct ThinWalletID {
        uint256 causeId;
        bytes thinWalletId;
    }

    struct QueuedItem {
        uint128 next; // The next item to be claimed in the queue. If this is the last item (at back of queue), next should be 0
        uint128 previous; // The previous item in the queue. If this is the first item (at the front of the queue), previous should be 0
        bytes32 id; // An unique identifier for the queue item. This will link the add to queue and withdrawal functions.
        bool isUnclaimed; // Set to true when enqueuing. We can delete the struct when dequeueing, saving some gas.
    }

    /// ### Events

    event RegisterCause(address indexed owner, address indexed daoToken, uint256 causeId);
    event RegisterWallet(address indexed walletAddress, ThinWalletID walletId);
    event WithdrawFromWallet(ThinWalletID wallet, WithdrawalRequest request);
    event UpdateCause(CauseRecord cause);
    event UpdateRewardAddress(
        address indexed oldRewardAddress,
        address indexed newAddress
    );
    event UpdateFee(uint256 oldFee, uint256 newFee);

    /// ### Functions

    /// @notice Creates a cause so that it can start using thin wallets that it controls
    /// @dev This should be an open function
    /// @param _cause  The cause to be registered
    function registerCause(CauseRegistrationRequest calldata _cause) external;

    /// @notice Updates a cause
    /// @dev Can only be called by the current owner of the cause
    /// @param _causeId  The cause to update
    /// @param _cause  The new details of the cause
    function updateCause(uint256 _causeId, CauseUpdateRequest calldata _cause)
        external;

    /// @notice Sets the address of the staking contract so rewards can be distributed
    /// @dev This should be secured
    /// @param _rewardContract  The new staking contract
    // function setRewardAddress(address _rewardContract) external;

    /// @notice Calculates the address that a given thin wallet is or will be deployed to
    /// @param _walletId  The wallet parameters to calculate the address from
    function calculateThinWallet(ThinWalletID calldata _walletId)
        external
        view
        returns (address wallet);

    /// @notice Deploys a thin wallet to the address derived from the parameters given
    /// @param _walletId  The wallet parameters
    /// @param _owners  The wallet owners to set. These accounts can transfer funds, so they should be limited
    function registerThinWallet(
        ThinWalletID calldata _walletId,
        address[] calldata _owners
    ) external;

    /// @notice Withdraws funds from the specified thin wallet
    /// @param _walletId  The wallet address parameters
    /// @param _withdrawal  An array of withdrawal requests for the wallet to process
    function withdrawFromThinWallet(
        ThinWalletID calldata _walletId,
        WithdrawalRequest calldata _withdrawal,
        bytes32 _proposalId
    ) external;

    /// @notice Allows the platform owner to set the platform fee
    /// @dev Only the platform owner should be able to call this
    /// @param _fee  The new platform fee
    function setPlatformFee(uint256 _fee) external;

    /// @notice Adds a queue which is linked to a unique identifier using the hash of combined causeID and proposalID
    /// @param _causeId The cause id
    /// @param _proposalId The proposal id
    function addToQueue(uint256 _causeId, bytes32 _proposalId) external;

    /// @notice Removes a queue arbitrarily at specified index
    /// @param _causeId The cause id
    /// @param _proposalId The proposal id
    /// @param _index The queue index
    function removeFromQueue(uint256 _causeId, bytes32 _proposalId, uint128 _index) external;

    /// @notice Gets queue item at specific index
    /// @param _causeId The cause id
    /// @param _index The queue index
    function getQueueAtIndex(uint256 _causeId, uint128 _index) external view returns (QueuedItem memory item);

    /// @notice Gets first item in queue
    /// @param _causeId The cause id
    function getFirstInQueue(uint256 _causeId) external view returns (uint128 queueFront);

    /// @notice Gets last item in queue
    /// @param _causeId The cause id
    function getLastInQueue(uint256 _causeId) external view returns (uint128 queueBack);

    /// ### Autogenerated getters

    function baseToken() external view returns (ERC20 baseToken);

    function stakingContract()
        external
        view
        returns (IStakingRewards stakingContract);

    function causeId() external view returns (uint256 causeId);

    function causeRecords(uint256 causeId)
        external
        view
        returns (
            address owner,
            address defaultWallet,
            address daoToken,
            uint256 rewardPercentage
        );

    function platformFee() external view returns (uint256 fee);

    function deployedWallets(bytes32 _salt)
        external
        view
        returns (address wallet);

    function walletImplementation()
        external
        view
        returns (address walletImplementation);
}
