pragma solidity 0.8.13;
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

import "../interfaces/IStakingRewards.sol";

interface IDonationsRouter {
    /// ### Structs
    struct CauseRegistrationRequest {
        address owner;
        uint256 rewardPercentage;
        address daoToken;
    }

    struct CauseRecord {
        address owner;
        address defaultWallet; /// Default wallet is calculated with cause Id and thin wallet id being equal to each other
        address daoToken;
        uint256 rewardPercentage; /// A PRBMath 60.18 fixed point number. 1e16 == 1% and 1e18 == 100%
    }

    struct WithdrawalRequest {
        address token;
        address recipient;
        uint256 amount;
    }

    struct ThinWalletID {
        uint256 causeId;
        bytes thinWalletId;
    }

    /// ### Events

    event RegisterCause(CauseRecord cause);
    event RegisterWallet(address indexed walletAddress, ThinWalletID walletId);
    event WithdrawFromWallet(ThinWalletID wallet, WithdrawalRequest request);
    event UpdateCause(CauseRecord cause);
    event UpdateRewardAddress(
        address indexed oldRewardAddress,
        address indexed newAddress
    );
    event UpdateFee(uint256 oldFee, uint256 newFee);

    /// ### Functions

    /// @notice Creates a cause so that it can start using thin wallets that it controls
    /// @dev This should be an open function
    /// @param _cause  The cause to be registered
    function registerCause(CauseRegistrationRequest calldata _cause) external;

    /// @notice Updates a cause
    /// @dev Can only be called by the current owner of the cause
    /// @param _causeId  The cause to update
    /// @param _cause  The new details of the cause
    function updateCause(
        uint256 _causeId,
        CauseRegistrationRequest calldata _cause
    ) external;

    /// @notice Sets the address of the staking contract so rewards can be distributed
    /// @dev This should be secured
    /// @param _rewardContract  The new staking contract
    // function setRewardAddress(address _rewardContract) external;

    /// @notice Calculates the address that a given thin wallet is or will be deployed to
    /// @param _walletId  The wallet parameters to calculate the address from
    function calculateThinWallet(ThinWalletID calldata _walletId)
        external
        view
        returns (address wallet);

    /// @notice Deploys a thin wallet to the address derived from the parameters given
    /// @param _walletId  The wallet parameters
    /// @param _owners  The wallet owners to set. These accounts can transfer funds, so they should be limited
    function registerThinWallet(
        ThinWalletID calldata _walletId,
        address[] calldata _owners
    ) external;

    /// @notice Withdraws funds from the specified thin wallet
    /// @param _walletId  The wallet address parameters
    /// @param _withdrawal  An array of withdrawal requests for the wallet to process
    function withdrawFromThinWallet(
        ThinWalletID calldata _walletId,
        WithdrawalRequest calldata _withdrawal
    ) external;

    /// @notice Allows the platform owner to set the platform fee
    /// @dev Only the platform owner should be able to call this
    /// @param _fee  The new platform fee
    function setPlatformFee(uint256 _fee) external;

    /// ### Autogenerated getters

    function baseToken() external view returns (ERC20 baseToken);

    function stakingContract()
        external
        view
        returns (IStakingRewards stakingContract);

    function causeId() external view returns (uint256 causeId);

    function causeRecords(uint256 causeId)
        external
        view
        returns (
            address owner,
            address defaultWallet,
            address daoToken,
            uint256 rewardPercentage
        );

    function platformFee() external view returns (uint256 fee);

    function deployedWallets(bytes32 _salt)
        external
        view
        returns (address wallet);

    function walletImplementation()
        external
        view
        returns (address walletImplementation);
}
","keccak256":"0xd0668827117a25a90eab3b78c3653596fcfe912f4b5e4a8004bac0fab690a7b3","license":"MIT"},"contracts/interfaces/IStakingRewards.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.13;
import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

interface IStakingRewards {
    /// ### Structs
    struct RewardDistribution {
        uint256 totalStake;
        uint256 rewardPerToken; // Needs to be boosted by 1e18 before storing so we don't lose precision
    }
    // If totalstake is 0, first staker gets everything. When ejecting, or withdrawing, and totalstake becomes 0, reward per token should also be set to 0.
    struct UserStake {
        uint256 stakedAmount;
        uint256 rewardEntry;
        uint256 pendingRewards;
        uint256 timeStaked;
    }

    /// ### Events
    event Stake(address indexed user, address indexed daoToken, uint256 amount);
    event Unstake(
        address indexed user,
        address indexed daoToken,
        uint256 amount
    );
    event ClaimRewards(
        address indexed user,
        address indexed daoToken,
        uint256 amount
    );
    event Distribution(address indexed daoToken, uint256 amount);
    event Eject(
        address indexed sender,
        address indexed daoToken,
        uint256 amount
    );

    /// ### Functions
    /// @notice Updates the lock up period in state
    /// @param _lockupPeriod The new 
    function setLockupPeriod(uint256 _lockupPeriod) external;

    /// @notice Stakes amount of dao token into the contract
    /// @param _daoToken The governance token of the dao to be staked.
    /// @param _amount The amount of governance token to be staked
    function stake(address _daoToken, uint256 _amount) external;

    /// @notice Stakes amount of dao token into the contract for a user
    /// @param _user The user the tokens are being staked on behalf for
    /// @param _daoToken The governance token of the dao to be staked.
    /// @param _amount The amount of governance token to be staked
    function stakeOnBehalf(address _user, address _daoToken, uint256 _amount) external;

    /// @notice Unstakes and transfers out a given amount of dao governance tokens
    /// @dev This function will also claim any outstanding rewards for the user
    /// @param _daoToken The dao governance token to unstake
    /// @param _amount The amount to remove
    /// @param _to The destination for the dao tokens and rewards
    function unstake(
        address _daoToken,
        uint256 _amount,
        address _to
    ) external;

    /// @notice Claims all outstanding rewards for the user for the specified dao
    /// @param _daoToken The dao to claim rewards for.
    /// @param _to The destination for the rewards
    function claimRewards(address _daoToken, address _to) external;

    /// @notice Withdraws the user's dao governance tokens without claiming any outstanding rewards
    /// @dev To be used as an emergency exit hatch for users. This will permanently lock any outstanding rewards for the user in the contract.
    /// @param _daoToken The dao governance token to eject.
    /// @param _to The destination wallet for the ejected tokens
    function emergencyEject(address _daoToken, address _to) external;

    /// @notice Updates the dao distribution to include an additional amount
    /// @dev This should pull tokens from the caller's account
    /// @dev This should be coded in such a way as to not require any kind of authorization other than providing an amount of reward tokens.
    /// @dev This should handle cases where there aren't any tokens staked: first staker gets everything.
    /// @param _daoToken The dao to distribute rewards to
    /// @param _amount The amount of reward token to distribute
    function distributeRewards(address _daoToken, uint256 _amount) external;

    /// @notice Returns the amount of claimable reward tokens for a user for a given dao
    /// @param _user The user who staked the tokens
    /// @param _daoToken The governance token of the dao the user has staked
    /// @return rewardAmount The amount of the reward token that the user is entitled to
    function pendingRewards(address _user, address _daoToken)
        external
        view
        returns (uint256 rewardAmount);

    /// ### Autogenerated getter functions

    function lockupPeriod() external view returns (uint256);

    function rewardToken() external view returns (ERC20 rewardToken);

    function daoRewards(address _daoToken)
        external
        view
        returns (uint256 totalStake, uint256 rewardPerToken);

    function userStakes(address _daoToken, address _user)
        external
        view
        returns (
            uint256 stakedAmount,
            uint256 rewardEntry,
            uint256 pendingRewards,
            uint256 timeStaked
        );
}
","keccak256":"0x01a00a2d4c8222edf80b8762d7e9e4b959ee1093cc6a8b4f12c678dd2f2e52af","license":"MIT"},"contracts/interfaces/IThinWallet.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.13;

interface IThinWallet {
  error InvalidPermissions(address _user);

  struct TokenMovement {
    address token;
    address recipient;
    uint256 amount;
  }

  struct EtherPaymentTransfer {
    address recipient;
    uint256 amount;
  }

  /// ### Events
  event TransferERC20(TokenMovement[] transfers);
  event TransferEther(EtherPaymentTransfer[] transfers);

  /// ### Functions
  /// @notice Initializes the thin wallet clone with the accounts that can control it
  /// @param _admin  This is should be set as the default admin. This will be the donation router
  /// @param _owners  The accounts that can call the transfer functions
  function initialize(address _admin, address[] calldata _owners) external;

  /// @notice Transfers amounts of an ERC20 to one or more recipients
  /// @dev If the `setApprove` field is true, the contract should approve that recipient for type(uint256).max
  /// @param _transfers  An array of transfers. Each transfer object specifies the amount and recipient to send tokens to
  function transferERC20(
    TokenMovement[] calldata _transfers
  ) external;

  /// @notice Transfers amounts of ether to one or more recipeints
  /// @dev This should use address(recipient).call to transfer the ether
  /// @param _transfers  The ether transfers
  function transferEther(EtherPaymentTransfer[] calldata _transfers) external;

  // TODO: Needs a receive function for ether transfers: receive() external payable { }
}
